const data = function () {

  const books = [];

  const addToData = (arrOfBooks) => {
    data.books = arrOfBooks;
  }
  
  return {
    books,
    addToData
  };


}();




const api = function () {

  const api_key = 'AIzaSyAGzmFLdfK9OWcRwEzoIO6DAVThBSBhyNQ';

  const url = 'https://www.googleapis.com/books/v1/volumes';

  let searchTerm = '';

  const getBooksFromGoogle = (searchTerm,callback) => {
    let queryObj = {
      q:searchTerm,
      key:api_key,
      // maxResults:30,


    };


    $.getJSON(url,queryObj,callback);
  };


  return {
    searchTerm,
    getBooksFromGoogle

  };
}();


const visualize = function () {

  const generateHTML = (book) => {
    try {
    return `
    <li class='book'>
      <span>${book.title}</span>
      <br>
      <a href='${book.previewLink} target="_blank"'><img src='${book.thumbnail}'></a>
      <br>
      <span>${book.author}</span>
      </li>`
    } catch(e) {
      console.log('One or more Books may not be rendered correctly due to an error in reading a property value. Please see the error below' + e);
    }
  }
  
  const generateDomString = () => {
    let domString = '';
    (data.books).forEach((book) => {
      domString += generateHTML(book);
    }); 
    return domString;
  }

  const render = (domString) => {
    $('.results').html(domString);
  };

  const generateBooksArray = (response) => {
    return response.items.map((item) => {
      try {
      return {
        title:item.volumeInfo.title,
        subtitle:item.volumeInfo.subtitle,
        author:item.volumeInfo.authors,
        publisher:item.volumeInfo.publisher,
        thumbnail:item.volumeInfo.imageLinks.thumbnail,
        previewLink:item.volumeInfo.previewLink
      }
    } catch (e) {
      console.log(e);
    }
    });
  };

  const handleSearch = () => {
    $('.book-search-form').on('submit', (event) => {
      event.preventDefault();
      api.searchTerm = $('.book-search-input').val();
      api.getBooksFromGoogle(api.searchTerm,(response) => {
        console.log('callback is ran');
        data.addToData(generateBooksArray(response))
        let domString = generateDomString();
        render(domString);
        console.log('render ran');
      });
    })
  }
  return {
    generateBooksArray,
    handleSearch,
    render,
    generateDomString,
    generateHTML
  }  
}();

$(visualize.handleSearch());